import './Blog.css'
import { useParams } from "react-router-dom";
import Axios from'axios'



function Blog(){

    const[data, setData] = React.useState('');
    const{post} = useParams();
    React.useEffect(() => {
        fetchData(post);
    }, []);

    const fetchData = async(post) =>{

        if(data == null){
          try{
            const response = await Axios.get('http://localhost:3001/blogs/'+post, {responseType: Blob});
            if(!response.ok){
                console.error('Error in loading posts: ');
            }
            const jsonData = new Blog([response.data])
            console.log(jsonData);
            setData(jsonData)
        }catch(error){
            console.error('Error in loading posts: ',error)
        }
      }
    }  

    console.log(data)

    return (<p>ELLO {data}</p>)
}

export default Blog;
