import React, {useState, useEffect} from "react";

function RecentPosts(){
    const[data, setData] = useState(null);

    useEffect(() => {
        fetchData();
    });

    const fetchData = async() =>{
        try{
            const response = await fetch('http://localhost:3001/recentPosts');
            if(!response.ok){
                console.error('Error in loading posts: ');
            }
            const jsonData = await response.json();
            setData(jsonData)
        }catch(error){
            console.error('Error in loading posts: ',error)
        }
    }   

    return(<div>
        <h3>Recent Posts</h3>
        {data ? (
          <ul>
            {data.map((item) => (<div ><p><h3><a href={item.post_link}>  {item.post_title} </a></h3></p><i> in  {item.category} </i></div>))}
          </ul>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    );
}

export default RecentPosts;
