import React, {useState, useEffect} from "react";

function RecentPosts(){
    const[data, setData] = useState(null);

    useEffect(() => {
        (async () => {

        if (data.length === 0) {
          try {
            const response = await fetch('http://localhost:3001/recentPosts');
            if (!response.ok) {
              console.error('Error in loading posts: ');
            }
            const jsonData = await response.json();
            setData(jsonData);
          } catch (error) {
            console.error('Error in loading posts: ', error);
          }
        }
      })();
    });


    return(<div>
        {data ? (
          <ul>
            {data.map((item) => (<div key={item.id}><p><h3><a href={item.post_link}>  {item.post_title} </a></h3></p><i> in </i></div>))}
          </ul>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    );
}

export default RecentPosts;
