import React from "react";
import './Blog.css'
import { useParams } from "react-router-dom";



function Blog(){

    const[data, setData] = React.useState(null);
    const{post} = useParams();
    React.useEffect(() => {
        fetchData(post);
    }, []);

    const fetchData = async(post) =>{

        if(data == null){
          try{
            const response = await fetch('http://localhost:3001/blogs/'+post);
            if(!response.ok){
                console.error('Error in loading posts: ');
            }
            const jsonData = await response.json();
            setData(jsonData)
        }catch(error){
            console.error('Error in loading posts: ',error)
        }
      }
    }  

    return (<p>ELLO {data}</p>)
}

export default Blog;
